import { useEffect, useState } from "react";


function Carousel(props) {
    const [offset, setOffset] = useState(0)
    const [styles, setStyles] = useState({
        transform: `translateX(${offset}px)`
    })
useEffect(() => {
    console.log(Math.abs(offset));
    console.log((props.children.length - 1) * 1320);
    setStyles({
        transform: `translateX(${offset}px)`
    })
}, [offset])
useEffect(() => {
    const interval = setInterval(() => {
        setOffset(prev => prev - 1320)
        if(Math.abs(offset) >= (props.children.length - 1) * 1320) {
            setOffset(0)
        }
    }, 1000)

    return () => clearInterval(interval)
}, [])

  return (
    <article className="carousel">
      <div className="carousel__list" style={styles}>{props.children}</div>
      <div className="carousel__buttons">
        {props.children.map((e, i) => {
            return <button key={i} className="carousel__button"></button>
        })}
      </div>
    </article>
  );
}

export default Carousel;
