import "./css/App.css";
import Header from "./components/Header/Header";
import AboutPage from "./components/AboutPage/AboutPage";
import HomePage from "./components/HomePage/HomePage";
import { useState } from "react";
import { Route, Routes } from "react-router-dom"


function App() {
  let [curLang, setCurLang] = useState("ру");
  let [languages, setLanguages] = useState(["ру", "en"]);
  let translatedText = {
    ру: {
      header: {
        title: "юридическая компания",
        adress: "Одесса, Адмиральський пр., 3",
        list: ["о нас", "услуги", "команда", "публикации", "контакты"],
      },
    },
    en: {
      header: {
        title: "law company",
        adress: "Odessa, Admiral's av., 3",
        list: ["about", "services", "team", "publications", "contact"],
      },
    },
  };
  function setCurrentLanguage(e) {
    if (e.target.value !== curLang) {
      setCurLang(e.target.value);
    }
  }
  console.log(translatedText["en"]);


  return (
    <>
      <section className="advertising">
        <div className="advertising__container _container">
          <Header
            text={translatedText[curLang].header}
            languages={languages}
            curLang={curLang}
            setLanguage={setCurrentLanguage}
            links={translatedText["en"].list}
          ></Header>
        </div>
      </section>

      <Routes>
        <Route path="/" element={ <HomePage /> } />
        <Route path="/about" element={ <AboutPage /> } />

      </Routes>
    </>
  );
}

export default App;
