function Publications(props) {
  return (
    <section className="publications">
      <div className="publications__container _container">
        <span className="publications__upper _link">{props.upper}</span>
        <h2 className="publications__title _title">{props.title}</h2>
        <div className="publications__list">
          {props.article.map((e, i) => {
            return <article key={i} className="publications__item">
                <img style={ props.images[i] ? "" : { display: "none" } } src={props.images[i]} alt={`publication-image${i + 1}`} className="publications__item-image" />
                <h3 className="publications__item-title">{e.title}</h3>
                <div className="publications__item-date">{e.date}</div>
                <div className="publications__item-article">{e.article}</div>
            </article>;
          })}
        </div>
      </div>
    </section>
  );
}

export default Publications;
