function PublicationsEntry(props) {
  return (
    <section className="publicationsEntry">
      <div className="publicationsEntry__container _container">
        <div className="publicationsEntry__switcher">
          <div className="publicationsEntry__switcher_wrapper">
            <button className="publicationsEntry__button">
              {props.switcher[0]}
            </button>
            <button className="publicationsEntry__button">
              {props.switcher[1]}
            </button>
          </div>
        </div>
        <article className="publicationsEntry__cards">
          {props.publications.map((e, i) => {
            return  <article key={i} className="publications__item">
            <figure
              style={props.images[i] ? {} : { display: "none" }}
              className="publications__item-figure"
            >
              <img
                src={props.images[i]}
                alt={`publication-image${i + 1}`}
                className="publications__item-image"
              />
            </figure>
            <div className="publications__content">
              <h3 className="publications__content-title">{e.title}</h3>
              <div className="publications__content-date">{e.date}</div>
              <div className="publications__content-separator"></div>
              <div className="publications__content-article">
                {`${e.article.slice(0, 117)}...`}
              </div>
            </div>
          </article>
          })}
        </article>
      </div>
    </section>
  );
}

export default PublicationsEntry;
